mapper.update:
UPDATE BOARD
   SET BOARD_VIEWS = (SELECT BOARD_VIEWS+1 FROM BOARD WHERE BOARD_SEQ = #{board_seq})
 WHERE BOARD_SEQ = #{board_seq}

mapper.select:
	SELECT COUNT(*)
	  FROM BOARD_LIKE
     WHERE BOARD_SEQ = #{board_seq}
       AND USER_ID = #{user_id}

mapper.delete:
    DELETE
	  FROM BOARD_LIKE
     WHERE BOARD_SEQ = #{board_seq}
       AND USER_ID = #{user_id}
